import React from 'react';
import Modal from '../../components/UI/Modal/Modal';
import Aux from '../Aux';

const withErrorHandler = (WrappedContent, axios) => {
    return (props) => {
        return (
            <Aux>
                <Modal show>
                    Something didn't work!
                </Modal>
                <WrappedContent {...props} />
            </Aux>
        );
    }
}

export default withErrorHandler;
